-    private double chiSquare(final int[] expected, final int[] observed) {
-        double sumSq = 0.0d;
-        double dev = 0.0d;
-        for (int i = 0; i < observed.length; i++) {
-            dev = observed[i] - expected[i];
-            sumSq += dev * dev / expected[i];
-        }
-        return sumSq;
-    }           
